            this.$router.push({
                path: '/newPage',
                query: {
                    listid: item.id
                }
            })
        },
        getHeaderlink(val) {
            if (val == 0) {
                this.$router.push({
                    path: '/MyList'
                })
            } else
            if (val == 1) {
                this.$router.push({
                    path: '/trading-list'
                })
            } else if (val == 2) {
